def minimumTime(grid: List[List[int]]) -> int:
    m, n = len(grid), len(grid[0])
    directions = [(0, 1), (1, 0), (0, -1), (-1, 0)]
    pq = [(0, 0, 0)]  # (time, row, col)
    visited = set()

    while pq:
        time, row, col = heapq.heappop(pq)

        if (row, col) in visited:
            continue

        visited.add((row, col))

        if row == m - 1 and col == n - 1:
            return time

