def waysToReachTarget(target: int, types: List[List[int]]) -> int:
    MOD = 10**9 + 7

    # dp[i] will be the number of ways to get i points
    dp = [0] * (target + 1)
    dp[0] = 1  # There's one way to get 0 points: answer no questions

    for count, marks in types:
